// 主页
import React, { useState, useEffect } from 'react'
import {
    SearchOutlined
  } from '@ant-design/icons';
  import { Switch } from 'antd';

import '../../assets/css/homepage.css'
import iphone from '../../assets/images/iphone.png'
import electricity from '../../assets/images/电量.png'
import wifi from '../../assets/images/wifi.png'
import search from '../../assets/images/搜索.png'
import brightness from '../../assets/images/控制.png'
import launchpad from '../../assets/images/launchpad.png'
import bear from '../../assets/images/bear.png'
import vscode from '../../assets/images/vscode.png'
import facetime from '../../assets/images/facetime.png'
import safari from '../../assets/images/safari.png'
import terminal from '../../assets/images/terminal.png'
import mail from '../../assets/images/mail.png'
import github from '../../assets/images/github.png'
import song from '../../assets/images/song.jpg'

function Lockscreen(props) {

    var myDate = new Date()
    var time = myDate.toLocaleString()

    const [click, setClick] = useState('false')
    // const [display, setDisplay] = useState('none')
    const [mytime, setMytime] = useState(time)
    // let [sliderwidth, setSliderwidth] = useState('45px')

    console.log(sliderwidth);
    
    const OpenPopUpWindow = (e) => {
        // console.log(e.currentTarget);
        let target = e.currentTarget  //获取到当前触发事件的对象
    //    console.log(target.getAttribute('click'));
        let name = target.getAttribute('name')
   
        
        // 当点击的是iPhone图标时出现弹窗
        if (name === 'iphone') {
            // console.log('打开和关闭iPhone的弹窗');
            var popupwindowiphone = document.getElementsByClassName('popupwindowiphone')[0]
            if (click==='false') {
                setClick('true')
                popupwindowiphone.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowiphone.style.display = 'none'
                // console.log('已关闭弹窗')
            } 
        }

        // 当点击的是WiFi图标时出现弹窗
        if (name === 'wifi') {
            var popupwindowwifi = document.getElementsByClassName('popupwindowwifi')[0]
            if (click==='false') {
                setClick('true')
                popupwindowwifi.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowwifi.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }

        // 当点击的是search图标时出现弹窗
        if (name === 'search') {
            var popupwindowsearch = document.getElementsByClassName('popupwindowsearch')[0]
            if (click==='false') {
                setClick('true')
                popupwindowsearch.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowsearch.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }

        // 当点击的是屏幕亮度调节时出现弹窗
        if (name === 'brightness') {
            var popupwindowbrightness = document.getElementsByClassName('popupwindowbrightness')[0]
            if (click==='false') {
                setClick('true')
                popupwindowbrightness.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowbrightness.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }
      
     
    }

    
    const ReturnLockScreen = () => {
        props.history.push('/')
    }
   
    useEffect(() => {

        // 定时器实现时间的动态刷新
        setInterval(function () {
            // console.log('ok');
            let timenow = new Date().toLocaleString()
            setMytime(timenow)
        
        }, 1000)

   

    }, [])

  
   
    
    
   const onChange = (checked) => {
        console.log({checked});
    }

    // let checked = document.getElementsByClassName('Switch')[0].aria-checked
    // console.log(checked);


    // 拖动display进度条发生改变
    const Changeslilder = (e) => {
        // console.log('1');
        let rangeslider_fill = document.getElementsByClassName('rangeslider_fill')[0]  //获取到进度条的div
        // console.log(rangeslider_fill);
        let sliderwidth = parseInt(rangeslider_fill.style.width ) //获取到进度条宽度

        let sliderhandle = document.getElementsByClassName('rangeslider_handle')[0]  //获取到操作进度条改变的元素
        sliderhandle.onmousemove = () => {
            console.log(sliderwidth);
            
            sliderwidth = sliderwidth+10
            // setSliderwidth(parseInt(sliderwidth)+10)
        }
    }
   
    


        return (
            <div className='backimg' style={{backgroundImage: "url("+require("../../assets/images/wallpaper-day.jpg").default+")"}}>
                  <div className='homepagefull' >
                <div className='nightwind-prevent'>
                    <div className='sign flex flex-row items-center'>
                            <div
                                className=' ipone-icon flex-row inline-flex'
                                onClick={ (e)=>{OpenPopUpWindow(e)}}
                                // click={click}
                                name='iphone'
                            >
                                <img
                                    src={iphone} alt=""
                                   
                                />
                        </div>
                        <span className='font-semibold'>Bear</span>
                    </div>

                        {/*点击iPhone弹窗  */}
                    <div className='popupwindowiphone'  style={{display:'none'}}>
                        <ul className='pop1'>
                            <li>About This Mac</li>
                        </ul>
                        <ul className='pop2'>
                                <li>System Preferences...</li>
                                <li>App Store...</li>
                        </ul>
                        <ul className='pop3'>
                            <li>Recent Items</li>
                        </ul>
                        <ul className='pop4'>
                            <li>Force Quit...</li>
                        </ul>
                        <ul className='pop5'>
                                <li>Sleep</li>
                                <li>Restart...</li>
                                <li>Shut Down...</li>
                        </ul>
                        <ul className='pop6'>
                                <li onClick={()=>{ReturnLockScreen()}}>Lock Screen</li>
                                <li onClick={()=>{ReturnLockScreen()}}>Log Out Mengmeng He...</li>
                        </ul>
                    </div>
                        

                    <div className='various-icons flex flex-row justify-end items-center '>
                        <div className='electricity inline-flex flex-row'>
                            <span className='mt mt-1' >100%</span>
                            <img src={electricity} alt=""  />
                        </div>
                            <div
                                className='wifi inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name = 'wifi'
                            >
                           <img src={wifi} alt="" />
                        </div>
                        <div
                                className='search inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name = 'search'
                            >
                            <img src={search} alt="" />
                        </div>
                        <div
                                className='screen-brightness inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name='brightness'
                            >
                            <img src={brightness} alt="" />
                        </div>

                        {/* 点击亮度调节弹窗 */}
                            <div className='popupwindowbrightness'>
                            
                            <div className="gridcontainer grid">
                                    <div className=' control-grid p1 '>
                                        
                                        <div className='div1'>
                                            <div className='svgdiv'>
                                            <svg stroke="#999" fill="#333" stroke-width="0" viewBox="0 0 640 512" class="bg-gray-400 bg-opacity-25 text-gray-700 rounded-full p-2" height="32" width="32" xmlns="http://www.w3.org/2000/svg"><path d="M634.91 154.88C457.74-8.99 182.19-8.93 5.09 154.88c-6.66 6.16-6.79 16.59-.35 22.98l34.24 33.97c6.14 6.1 16.02 6.23 22.4.38 145.92-133.68 371.3-133.71 517.25 0 6.38 5.85 16.26 5.71 22.4-.38l34.24-33.97c6.43-6.39 6.3-16.82-.36-22.98zM320 352c-35.35 0-64 28.65-64 64s28.65 64 64 64 64-28.65 64-64-28.65-64-64-64zm202.67-83.59c-115.26-101.93-290.21-101.82-405.34 0-6.9 6.1-7.12 16.69-.57 23.15l34.44 33.99c6 5.92 15.66 6.32 22.05.8 83.95-72.57 209.74-72.41 293.49 0 6.39 5.52 16.05 5.13 22.05-.8l34.44-33.99c6.56-6.46 6.33-17.06-.56-23.15z"></path></svg>
                                            </div>
                                      
                                            <div className='text'>
                                                <span className='wifitext'>Wi-Fi</span>
                                                <span>Off</span>
                                            </div>
                                        </div>
                                        
                                        <div className='div2'>
                                            <div className='svgdiv'>
                                        <svg stroke="#333" fill="#fff" stroke-width="2" viewBox="0 0 24 24" stroke-linecap="round" stroke-linejoin="round" class="bg-blue-500 text-white rounded-full p-2" height="32" width="32" xmlns="http://www.w3.org/2000/svg"><polyline points="6.5 6.5 17.5 17.5 12 23 12 1 17.5 6.5 6.5 17.5"></polyline></svg>
                                            </div>
                                      
                                            <div className='text'>
                                                <span className='bluetoothtext'>Bluetooth</span>
                                                <span>On</span>
                                            </div>
                                        </div>
