// 主页
import React, { useState, useEffect } from 'react'
import {
    SearchOutlined
  } from '@ant-design/icons';
  import { Switch } from 'antd';

import '../../assets/css/homepage.css'
import iphone from '../../assets/images/iphone.png'
import electricity from '../../assets/images/电量.png'
import wifi from '../../assets/images/wifi.png'
import search from '../../assets/images/搜索.png'
import brightness from '../../assets/images/控制.png'
import launchpad from '../../assets/images/launchpad.png'
import bear from '../../assets/images/bear.png'
import vscode from '../../assets/images/vscode.png'
import facetime from '../../assets/images/facetime.png'
import safari from '../../assets/images/safari.png'
import terminal from '../../assets/images/terminal.png'
import mail from '../../assets/images/mail.png'
import github from '../../assets/images/github.png'
import song from '../../assets/images/song.jpg'

function Lockscreen(props) {

    var myDate = new Date()
    var time = myDate.toLocaleString()

    const [click, setClick] = useState('false')
    // const [display, setDisplay] = useState('none')
    const [mytime, setMytime] = useState(time)
    
    const OpenPopUpWindow = (e) => {
        // console.log(e.currentTarget);
        let target = e.currentTarget  //获取到当前触发事件的对象
    //    console.log(target.getAttribute('click'));
        let name = target.getAttribute('name')
        console.log(name);
        
        // 当点击的是iPhone图标时出现弹窗
        if (name === 'iphone') {
            // console.log('打开和关闭iPhone的弹窗');
            var popupwindowiphone = document.getElementsByClassName('popupwindowiphone')[0]
            if (click==='false') {
                setClick('true')
                popupwindowiphone.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowiphone.style.display = 'none'
                // console.log('已关闭弹窗')
            } 
        }

        // 当点击的是WiFi图标时出现弹窗
        if (name === 'wifi') {
            var popupwindowwifi = document.getElementsByClassName('popupwindowwifi')[0]
            if (click==='false') {
                setClick('true')
                popupwindowwifi.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowwifi.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }

        // 当点击的是search图标时出现弹窗
        if (name === 'search') {
            var popupwindowsearch = document.getElementsByClassName('popupwindowsearch')[0]
            if (click==='false') {
                setClick('true')
                popupwindowsearch.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowsearch.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }

        // 当点击的是屏幕亮度调节时出现弹窗
        if (name === 'brightness') {
            var popupwindowbrightness = document.getElementsByClassName('popupwindowbrightness')[0]
            if (click==='false') {
                setClick('true')
                popupwindowbrightness.style.display = 'block'
                // setDisplay('block')
                // console.log('已打开弹窗')
            }
            else {
                setClick('false')
                // setDisplay('none')
                popupwindowbrightness.style.display = 'none'
                // console.log('已关闭弹窗')
            }
        }
      
     
    }

    
    const ReturnLockScreen = () => {
        props.history.push('/')
    }
   
    useEffect(() => {

        // 定时器实现时间的动态刷新
        setInterval(function () {
            // console.log('ok');
            let timenow = new Date().toLocaleString()
            setMytime(timenow)
        
        }, 1000)

      const  handleMouseUp = () => {
            let { UPDATE_STATE } = this.store.actions
            //点击页面空白区域,更多弹层消失
            document.onmouseup = () => {
                UPDATE_STATE({
                    Pinitarrowstate: true,
                    Sinitarrowstate: true,
                })
            }
        }
        window.addEventListener('mouseup', handleMouseUp, false)
        window.removeEventListener('mouseup', handleMouseUp, false)

    }, [])
    
   const onChange = (checked) => {
        console.log({checked});
    }

    // let checked = document.getElementsByClassName('Switch')[0].aria-checked
    // console.log(checked);
   
    


        return (
            <div className='backimg' style={{backgroundImage: "url("+require("../../assets/images/wallpaper-day.jpg").default+")"}}>
                  <div className='homepagefull' >
                <div className='nightwind-prevent'>
                    <div className='sign flex flex-row items-center'>
                            <div
                                className=' ipone-icon flex-row inline-flex'
                                onClick={ (e)=>{OpenPopUpWindow(e)}}
                                // click={click}
                                name='iphone'
                            >
                                <img
                                    src={iphone} alt=""
                                   
                                />
                        </div>
                        <span className='font-semibold'>Bear</span>
                    </div>

                        {/*点击iPhone弹窗  */}
                    <div className='popupwindowiphone'  style={{display:'none'}}>
                        <ul className='pop1'>
                            <li>About This Mac</li>
                        </ul>
                        <ul className='pop2'>
                                <li>System Preferences...</li>
                                <li>App Store...</li>
                        </ul>
                        <ul className='pop3'>
                            <li>Recent Items</li>
                        </ul>
                        <ul className='pop4'>
                            <li>Force Quit...</li>
                        </ul>
                        <ul className='pop5'>
                                <li>Sleep</li>
                                <li>Restart...</li>
                                <li>Shut Down...</li>
                        </ul>
                        <ul className='pop6'>
                                <li onClick={()=>{ReturnLockScreen()}}>Lock Screen</li>
                                <li onClick={()=>{ReturnLockScreen()}}>Log Out Mengmeng He...</li>
                        </ul>
                    </div>
                        

                    <div className='various-icons flex flex-row justify-end items-center '>
                        <div className='electricity inline-flex flex-row'>
                            <span className='mt mt-1' >100%</span>
                            <img src={electricity} alt=""  />
                        </div>
                            <div
                                className='wifi inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name = 'wifi'
                            >
                           <img src={wifi} alt="" />
                        </div>
                        <div
                                className='search inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name = 'search'
                            >
                            <img src={search} alt="" />
                        </div>
                        <div
                                className='screen-brightness inline-flex flex-row'
                                onClick={(e) => { OpenPopUpWindow(e) }}
                                name='brightness'
                            >
                            <img src={brightness} alt="" />
                        </div>

                        {/* 点击亮度调节弹窗 */}
                            <div className='popupwindowbrightness'>
                            
                            <div className="gridcontainer grid">
                                    <div className=' control-grid p1 '>
                                        
                                        <div className='div1'>
                                       
                                        </div>
                                        <div className='div2'></div>
                                        <div className='div3'></div>
                                </div>
                                    <div className=' control-grid p2   col-span-2'>
                                        <div className='svg'></div>
                                        <div className='text'></div>
                                </div>
                                    <div className=' control-grid p3 flex '>
                                        <div className="svg">

                                        </div>
                                        <span className='text'>Keyboard Brightness</span>
                                </div>
                                    <div className=' control-grid p4 '>
                                        <div className="svg"></div>
                                        <span className='text'>Enter Fullscreen</span>
                                </div>
                            </div>
                                <div className=' control-grid p5 '>
                                    <span className='text'>Display</span>
                                    <div className='slider'></div>
                            </div>
                                <div className=' control-grid p6 '>
                                <span className='text'>Sound</span>
                                    <div className='slider'></div>
                            </div>
                                <div className=' control-grid p7'>
                                    <img src={song} alt="" />
                                    <div className='song'>
                                        <span className='font-medium'>Sunflower</span>
                                        <span className='text-xs'>Post Malone / Swae Lee</span>
                                    </div>
                                    <div className='svg'></div>
                            </div>
                        </div>

                            {/* 点击WiFi弹窗 */}
                            <div className='popupwindowwifi' style={{display:'none'}}>
                                <div className='wifitext'>Wi-Fi</div>
                                <div className='icon'>
                                <Switch className='Switch' defaultChecked onChange={onChange} />
                                </div>
                            </div>

                           <span> {mytime }</span>
                            {/* <span>
                                
                        Thu May 20
                        </span>
                        <span>
                        9:35 PM
                        </span> */}
                    </div>
                    </div>
                    
                    {/* 点击搜索出现的弹窗 */}
                    <div className='popupwindowsearch'  style={{display:'none'}}>
                        <div className='w-full'>
                            <div className='col-start'>
                                <SearchOutlined
                                    className='search-svg'
                                    style={{ width: '30px',height: '30px',fontSize:'28px', color: '#524e56',marginTop:'24px' }} />
                            </div>
                            <input
                                className='spotlight-input'
                                placeholder='Spotlight Search'
                                type="text"
                               
                                />
                        </div>
                    </div>


                <div className='bottom' >
                    <ul>
                        <li className='ml-end'>
                            <img src={launchpad} alt="" className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={bear} alt=""  className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={safari} alt=""  className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={vscode} alt="" className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={facetime} alt="" className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={terminal} alt="" className='willchange' />
                        </li>
                        <li className='ml-end'>
                            <img src={mail} alt="" className='willchange'/>
                        </li>
                        <li className='ml-end'>
                            <img src={github} alt="" className='willchange' />
                        </li>
                    </ul>
                </div>
                
                </div>
                

                
            </div>
            

        )

}


export default Lockscreen
