import { combineReducers } from 'redux'
import {
    AUTH_SUCCESS,
    ERROR_MSG,
  
} from './action-types'

const initUser = {
    username:'',//用户名
    msg:'',//错误提示信息
  
}

function user(state = initUser, action) {
    switch (action.type){
        case AUTH_SUCCESS:
            console.log(action.data);

            // return { ...action.data }
            return {...state,username:action.data}
        case ERROR_MSG:
            return { ...state, msg: action.data }
        default:
            return state
    }
  
}

export default combineReducers(
    {
        user,
    }
)
