// 重启页面
import React, {useState, useEffect } from 'react'
import { Progress } from 'antd'

import '../../assets/css/restartpage.css'
function Restartpage(props) {


    const [percent, setPercent] = useState(0)

    useEffect((props) => {
        // console.log('componentdidMount');
       const timer= setInterval(function (percent, props) {
           if (percent >= 100) {
                
            clearInterval(timer)
            props.history.push('/')
            window.location.href='http://localhost:3000/'
                
               
                
            }
            else {
                setPercent(percent => percent + 5)

               
                
            }
       }, 50)
        
       
      
    }, [])

   
   

    return (
        <div>
            <div className='backimg' style={{backgroundImage: "url("+require("../../assets/images/black.jpg").default+")"}}>
                <Progress
                    percent={percent}
                    showInfo={false}
                    className='progress'
                    // status='active'
                    strokeColor='#fff'
                    trailColor='#666'
                    />
            </div>
        </div>
    )
}

export default Restartpage
